mapper.select:
select * from restaurant_list
where REL_CALL = #{relCall}		
and zone_num = #{zoneNum}
and REL_STRING_CATEGORY = #{relStringCategory}

select * from restaurant_list rel 
inner join theme_list thl on thl.REL_NUM = rel.REL_NUM
where ${themeKey} = ${themeValue}

mapper.update:
UPDATE restaurant_list
REL_NAME = #{relName}, REL_CATEGORY = #{relCategory}, REL_STRING_CATEGORY = #{relStringCategory}, REL_ETC_TIME = #{relEtcTime}, REL_LATITUDE = #{relLatitude}, REL_LONGITUDE = #{relLongitude}, ZONE_NUM = #{zoneNum}, SUBWAY_NUM = #{subwayNum}, REL_SUB_ADDRESS = #{relSubAddress}, REL_CALL = #{relCall}, REL_MODDAT = DATE_FORMAT(NOW(),'%Y%m%d'), REL_MODTIM = DATE_FORMAT(NOW(),'%H%i%S')
WHERE REL_NUM = #{relNum}
