mapper.update:
update review_info
REI_TITLE = #{reiTitle}, REI_DATE = #{reiDate}, REI_TIME = #{reiTime}, REI_THEME = #{reiTheme}, REI_CONTENTS = #{reiContents}, REI_IMG1_NAME = #{reiImg1Name}, REI_IMG2_NAME = #{reiImg2Name}, REI_IMG3_NAME = #{reiImg3Name}, REI_MODDAT = DATE_FORMAT(NOW(),'%Y%m%d'), REI_MODTIM = DATE_FORMAT(NOW(),'%H%i%S')
WHERE REI_NUM = #{reiNum}
